class Camisa:
    def __init__(self, tamanho:str):
        self.__tamanho = ""

    def set_tamanho(self, tamanho:str)-> bool:
        opcoes = ['PP', 'P', 'M', 'G', 'GG', 'XG']
        if tamanho in opcoes:
            self.__tamanho = tamanho 
            print("O tamanho do sua camisa é: {self.tamanho}")
        return False
